mapper.select:
select
com_id, com_message, com_nick, com_rate, test1, test2
from commit


        and com_id = #{comId}


        and com_message = #{comMessage}


        and com_nick = #{comNick}


        and com_rate = #{comRate}


        and test1 = #{test1}


        and test2 = #{test2}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from commit


        and com_id = #{comId}


        and com_message = #{comMessage}


        and com_nick = #{comNick}


        and com_rate = #{comRate}


        and test1 = #{test1}


        and test2 = #{test2}
