mapper.select:
select
o_id, o_price, o_type, o_posyfee, o_status, o_createtime, o_updatetime, o_paytime, o_consigntime, o_endtime,
t_id, u_id, gid, test1, test2
from orders


        and o_id = #{oId}


        and o_price = #{oPrice}


        and o_type = #{oType}


        and o_posyfee = #{oPosyfee}


        and o_status = #{oStatus}


        and o_createtime = #{oCreatetime}


        and o_updatetime = #{oUpdatetime}


        and o_paytime = #{oPaytime}


        and o_consigntime = #{oConsigntime}


        and o_endtime = #{oEndtime}


        and t_id = #{tId}


        and u_id = #{uId}


        and gid = #{gid}


        and test1 = #{test1}


        and test2 = #{test2}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from orders


        and o_id = #{oId}


        and o_price = #{oPrice}


        and o_type = #{oType}


        and o_posyfee = #{oPosyfee}


        and o_status = #{oStatus}


        and o_createtime = #{oCreatetime}


        and o_updatetime = #{oUpdatetime}


        and o_paytime = #{oPaytime}


        and o_consigntime = #{oConsigntime}


        and o_endtime = #{oEndtime}


        and t_id = #{tId}


        and u_id = #{uId}


        and gid = #{gid}


        and test1 = #{test1}


        and test2 = #{test2}
