mapper.select:
select
sh_id, gid, sh_number, u_id, sh_ctime, test1, test2
from shoppingcar


        and sh_id = #{shId}


        and gid = #{gid}


        and sh_number = #{shNumber}


        and u_id = #{uId}


        and sh_ctime = #{shCtime}


        and test1 = #{test1}


        and test2 = #{test2}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from shoppingcar


        and sh_id = #{shId}


        and gid = #{gid}


        and sh_number = #{shNumber}


        and u_id = #{uId}


        and sh_ctime = #{shCtime}


        and test1 = #{test1}


        and test2 = #{test2}
